import { uiActions } from "./ui-slice";

export const fetchCartData = () => {
    return dispatch => {
        const fetchData = async () => {
            const repsonse = await fetch("https://test-project-5e0cb-default-rtdb.firebaseio.com/cart.json" //Dont need to add the second parameter GET because it is the default
            )
            if(!respnse.ok){
                throw new Error('Could not fetch cart data')
            }
            const data = await response.json()
            return data
        }
        
    }
}
export const sendCartData = (cart) => {
    return async (dispatch) => {
      dispatch(
        uiActions.showNotification({
          status: "pending",
          title: "Sending...",
          message: "Sending Cart Data",
        })
      );
      const sendRequest = async () => {
        const response = await fetch(
          "https://test-project-5e0cb-default-rtdb.firebaseio.com/cart.json",
          {
            method: "PUT",
            body: JSON.stringify(cart),
          }
        );
        if (!response.ok) {
          throw new Error("Sending cart data failed");
        }
      };
      try {
        await sendRequest();
  
        dispatch(
          uiActions.showNotification({
            status: "success",
            title: "Success!",
            message: "Sent Cart Data Sucessfully",
          })
        );
      } catch (error) {
        dispatch(
          uiActions.showNotification({
            status: "error",
            title: "Error!",
            message: "Sending Cart Data Failed",
          })
        );
      }
    };
  };
